import axios from 'axios';
import Qs from 'qs';
import {onTips} from '@/assets/js/common.js';
import Crumb from '@/components/Crumb.vue'
export default {
    name: 'Group',
    components: {
        Crumb
    },
    data() {
        return {
            userData: [],
            dialogAdd: false,
            addStatus: false,
            add: {
                username: '',
                realname: '',
                level: '',
                password: ''
            },
            dialogEdit: false,
            editStatus: false,
            edit: {
                id: '',
                username: '',
                realname: '',
                password: '',
                oldpwd: ''
            },
            dialogDelete: false,
            del: {
                id: '',
                index: ''
            },
            options: [
                {value: 0, label: '超级管理员', disabled: true},
                {value: 1, label: '管理员'},
                {value: 2, label: '用户'}
            ]        
        }
    },
    created() {
        let that = this;
        axios.post('/api/list', {}, {})
        .then(function(res) {
            if (res.data.status) {
                that.userData = res.data.data;
            } else {
                onTips(that, 'error', res.data.info);
            }
        })
        .catch(function(err) {
            onTips(that, 'error', '网络错误～');
        })
    },
    watch: {
        add: {
            handler: function() {
                if (this.add.username != '' && this.add.realname != '' && this.add.password != '') {
                    this.addStatus = true;
                } else {
                    this.addStatus = false;
                }
            },
            deep: true
        },
        edit: {
            handler: function() {
                if (this.edit.username != '' && this.edit.realname != '' && this.edit.password != '******') {
                    this.editStatus = true;
                } else {
                    this.editStatus = false;
                }
            },
            deep: true
        }
    },
    methods: {
        handleAdd(index, row) {
            this.dialogAdd = true;
        },
        handleEdit(index, row) {
            this.dialogEdit = true;
            this.edit.id = row.id;
            this.edit.username = row.username;
            this.edit.realname = row.realname;
            this.edit.password = '';
            this.edit.index = index;
        },
        handleDelete(index, row) {
            this.dialogDelete = true;
            this.del.id = row.id;
            this.del.index = index;
        },
        onAdd: function() {
            let that = this;
            let data = {
                level: that.add.level,
                username: that.add.username,
                realname: that.add.realname,
                password: that.add.password
            };
            axios.post('/api/add', Qs.stringify(data), {})
            .then(function(res) {
                if (res.data.status) {
                    that.dialogAdd = false;
                    that.userData.push(res.data.data[0]);
                    onTips(that, 'success', res.data.info);
                } else {
                    onTips(that, 'error', res.data.info);
                }
            })
            .catch(function(err) {
                onTips(that, 'error', '网络错误～');
            })
        },
        onEdit: function() {
            let that = this;
            let data = {
                id: that.edit.id,
                username: that.edit.username,
                realname: that.edit.realname,
                password: that.edit.password,
                oldpwd: that.edit.oldpwd
            };

            axios.post('/api/edit', Qs.stringify(data), {})
            .then(function(res) {
                if (res.data.status) {
                    let index = that.edit.index;
                    that.userData[index].username = that.edit.username;
                    that.userData[index].realname = that.edit.realname;                    
                    that.dialogEdit = false;
                    onTips(that, 'success', res.data.info);
                } else {
                    onTips(that, 'error', res.data.info);
                }
            })
            .catch(function(err) {
                onTips(that, 'error', '网络错误～');
            })
        },
        onDelete: function() {
            let that = this;
            this.dialogDelete = false;
            let data = {
                id: that.del.id
            };
            axios.post('/api/delete', Qs.stringify(data), {})
            .then(function(res) {
                if (res.data.status) {
                    that.userData.splice(that.del.index, 1);
                    onTips(that, 'success', res.data.info);
                } else {
                    onTips(that, 'error', res.data.info);
                }
            })
            .catch(function(err) {
                onTips(that, 'error', '网络错误～');
            })
        },
        onLevel: function(level) {
            let _level;
            switch(level)
            {
                case 0:
                  _level =  '超级管理员';
                  break;
                case 1:
                  _level =  '管理员';
                  break;
                case 2:
                  _level =  '用户';
                  break;
            }
            return _level;
        }
    }
}
